mapper.update:
update tbl_book set in_out=#{in_out}, sum=#{sum},
classify=#{classify}, spend_type=#{spend_type}, other=#{other}
where
no=#{no}

mapper.select:
		
		select * from tbl_book
		where no>0
		

		
			and enterdate >= #{first_date}
		

		
			and enterdate <= #{last_date}
		

		
			and between #{first_date} and #{last_date}
		

		
			and in_out = #{in_out}
		

		
			and classify = #{classify}
		

		
			and spend_type = #{spend_type}
		

		
		order by no desc, enterdate desc
